def has_cycle(graph):
    visited = set()
    stack = set()
    def visit(node):
        if node in stack:
            return True
        if node in visited:
            return False
        stack.add(node)
        for neighbor in graph.get(node, []):
            if visit(neighbor):
                return True
        stack.remove(node)
        visited.add(node)
        return False
    return any(visit(n) for n in graph)

