def levenshtein(s1, s2):
    if len(s1)<len(s2):
        return levenshtein(s2,s1)
    if len(s2)==0:
        return len(s1)
    prev=range(len(s2)+1)
    for i,c1 in enumerate(s1):
        curr=[i+1]
        for j,c2 in enumerate(s2):
            insert=prev[j+1]+1
            delete=curr[j]+1
            replace=prev[j]+(c1!=c2)
            curr.append(min(insert,delete,replace))
        prev=curr
    return prev[-1]

